import React, { useState } from "react";
import avatarImage from "./i1.jpg"; // Import the image
import axios from "axios";

function Signin()
{
    let [name,setname] = useState();
    let [email,setmail] = useState();
    let [pass,setpass]=useState();
    let [role,setrole]=useState();

    let after =()=>{
        setname("");
        setmail("");
        setpass("");
        setrole("");
    }
    let Post = async ()=>{
        try{
            let r = await axios.get("http://localhost:2001/userinfo",{
                params:{name,email,pass,role},withCredentials:true
            });
            alert(r.data);
            after();
        }
        catch(err)
        {
            alert("Credentials not found");
        }
    }

    return(
        <>
        
            <div className="all">
        <div className="im1" >
        <img src={avatarImage} style={{width:"300px" , height:"300px"}} alt="avatar"></img>
        </div>
                <div className="inp1">
                 Name:
                </div>
                <input type="text" placeholder="name" className="inp2" value={name} onChange={(e)=>{
                    setname(e.target.value);
                }} required></input>

                
                <div className="inp1">
                 Email:
                </div>
                <input type="email" placeholder="Email" className="inp2" value={email} onChange={(e)=>{
                    setmail(e.target.value);
                }} required></input>

                <div className="inp1">
                 Password:
                </div>
                <input type="password" placeholder="Password" className="inp2" value={pass} onChange={(e)=>{
                    setpass(e.target.value);
                }} required></input>
                <br/>
               <input type="radio" id="Admin" name="fav_language" value="Admin"  onChange={(e)=>{
                    setrole("Admin");
                }} className="inp4"/>
                <label for="Admin" className="inp5">Admin</label><br/>
                <input type="radio" id="Customer" name="fav_language" value="Customer" onChange={(e)=>{
                    setrole("Customer");
                }}  className="inp4"/>
                <label for="Customer" className="inp5">Customer</label><br></br>
                <div className="i3">
                
                <button type="button" className="button1 inp3" onClick={Post}>Submit</button>
                </div>

            </div>
        </>
    )
}

export {Signin};
