import React, { useState } from "react";
import avatarImage from "./i1.jpg"; // Import the image
import axios from "axios";

function Addproducts()
{
    let [name,setname]=useState();
    let [quan,setqty]=useState();
    let [cat,setcat]=useState();
    let [im,setim]=useState();

    let after = ()=>
    {
        
        setname("");
        setqty("");
        setcat("");
        setim("");
    }
    let post = async()=>{
        try{
            if(cpassword==password)
            {
                
                let g = await axios.post("http://localhost:2001/userinfo",{
                    name,phone,email,password
                });
                after();
                alert(g.data);
            }
            else{
                alert("password are same");
            }
        }
        catch(err)
        {
          alert("registration not successfly");
        }
    }
    return(
        <>
        
            <div className="all">
        <div className="im1" >
        <img src={avatarImage} style={{width:"300px" , height:"300px"}} alt="avatar"></img>
        </div>
                <div className="inp1">
                 Name:
                </div>
                <input type="text" placeholder="name" className="inp2" value={name} onChange={(e)=>{
                    setname(e.target.value);
                }} required></input>

                <div className="inp1">
                 Quantity
                </div>
                <input type="password" placeholder="Password" className="inp2" value={password} onChange={(e)=>{
                    setqty(e.target.value);
                }} required></input>
                
                <div className="inp1">
                Confirm  Password:
                </div>
                <input type="password" placeholder="Confirm Password" className="inp2" value={cpassword} onChange={(e)=>{
                    setcpass(e.target.value);
                }} required></input>

<div className="inp1">
                Pic:
                </div>
                <input type="password" placeholder="Confirm Password" className="inp2" value={cpassword} onChange={(e)=>{
                    setim(e.target.value);
                }} required></input>

<div className="inp1">
  Category:
</div>
<select className="inp2" value={cat} onChange={(e) => setcat(e.target.value)}>
  <option value="grocery">Grocery</option>
  <option value="electronics">Electronics</option>
  <option value="stationary">Stationary</option>
  <option value="fastfood">Fast Food</option>
</select>

                <div className="i3">

                <button type="button" className="button1 inp3" onClick={post}>Submit</button>
                </div>

            </div>
        </>
    )
}

export {Addproducts};
