import axios from "axios";
import React, { useEffect, useState } from "react";

function Car({ name, category, qty, pic, price }) {
    const [quantity, setQuantity] = useState(qty);
  
    const increment = () => {
      setQuantity(quantity + 1);
    };
  
    const decrement = () => {
      if (quantity > 0) {
        setQuantity(quantity - 1);
      }
    };
  
    return (
      <div className="mt-5 ms-5 mb-5 me-5 card" style={{ width: "18rem", display: "inline-block" }}>
        <img src={pic} className="card-img-top" alt={name} />
        <div className="card-body">
          <h5 className="card-title">Name: {name}</h5>
          <p className="card-text">Category: {category}</p>
          <p className="card-text">Quantity: {quantity}</p>
          <p className="card-text">Price: {price}</p>
          <div className="quantity-control">
            <button className="decrement-button ms-3" onClick={decrement}>-</button>
            <span className="quantity mx-5">{quantity}</span>
            <button className="increment-button mx-2" onClick={increment}>+</button>
          </div>
          <button className="add-to-cart-button mx-2">Add to Cart</button>
        </div>
      </div>
    );
  }

function Cuscard(props) {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get("http://localhost:2001/home",{withCredentials:true});
        console.log(response.data);
        setData(response.data);
      } catch (err) {
        console.log(err);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      {data.map((item, index) => (
  (item.name.includes(props.msg) )? (
   
    <Car
      key={index}
      name={item.name}
      category={item.category}
      qty={item.qty}
      pic={item.pic}
      price={item.price}
    />
  ) : null
))}

    </div>
  );
}

export { Cuscard };
