import axios from "axios";
import React, { useEffect, useState, } from "react";
import { useParams } from "react-router-dom";


function Sta() {
  let [data1,setr] = useState("false");
  const [data, setData] = useState([]);
  let {name} = useParams();
  console.log("param:",name);

  function Car({ name, category, qty, pic,data1 }) {
    setr(data1);
    return (
      <div className="mt-5 ms-5 mb-5 me-5 card" style={{width: "18rem",display:"inline-block" }}>
        <img src={pic} className="card-img-top" alt={name} />
        <div className="card-body">
          <h5 className="card-title">Name : {name}</h5>
          <p className="card-text"> Category: {category}</p>
          <p className="card-text"> Quantity: {qty}</p>
        </div>
      </div>
    );
  }

  
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get("http://localhost:2001/home");
        console.log(response.data);
        setData(response.data);
      } catch (err) {
        console.log(err);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      {data.map((item, index) => (
  item.category == name ? (
    <Car
      key={index}
      name={item.name}
      category={item.category}
      qty={item.qty}
      pic={item.pic}
      data1={"true"}
    />
  ) : null
))}

    </div>
  );
}

export { Sta };
