import axios from "axios";
import React, { useEffect, useState, } from "react";
import { useParams,useNavigate } from "react-router-dom";
import { Error } from "./Error";
function Car({ name, category, qty, pic }) {
  return (
    <div className="mt-5 ms-5 mb-5 me-5 card" style={{width: "18rem",display:"inline-block" }}>
      <img src={pic} className="card-img-top" alt={name} />
      <div className="card-body">
        <h5 className="card-title">Name : {name}</h5>
        <p className="card-text"> Category: {category}</p>
        <p className="card-text"> Quantity: {qty}</p>
      </div>
    </div>
  );
}

function Sta() {
  const [data1, setData1] = useState("false");
  const [data, setData] = useState([]);
  let r = useNavigate();
  let {name} = useParams();
  console.log("param:",name);
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get("http://localhost:2001/home");
        console.log(response.data);
        setData(response.data);
        data.map((val,ind)=>{
          if(val.category==name)
          {
            setData1("true");
          }
        })
        console.log(data1);
      } catch (err) {
        console.log(err);
      }
    }

    fetchData();
    
  }, []);

if(data1=="true")
{

  return (
    <div>
      {data.map((item, index) => (
  item.category == name ? (
    <Car
      key={index}
      name={item.name}
      category={item.category}
      qty={item.qty}
      pic={item.pic}
    />
  ) : null
))}
</div>
  );
}
else{
return(
  <Error></Error>
)
}

}
    

export { Sta };
