import React, { useState } from "react";
import avatarImage from "./i1.jpg"; // Import the image
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Signin() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("");
  const [role, setRole] = useState("Customer"); // Default role
  let navigate = useNavigate();
  const clearFields = () => {
    setName("");
    setEmail("");
    setPass("");
  };

  const handleRoleChange = (e) => {
    setRole(e.target.value);
  };

  const handleSubmit = async () => {
    try {
      const response = await axios.get("http://localhost:2001/userinfo", {
        params: { name, email, pass, role },
        withCredentials: true
      });
      alert(response.data);
      document.getElementById("f1").style.display="none";
      navigate("/home");
    } catch (err) {
      alert("Credentials not found");
    }
  };

  return (
    <>
      <div className="all">
        <div className="im1">
          <img src={avatarImage} style={{ width: "300px", height: "300px" }} alt="avatar" />
        </div>
        <div className="inp1">Name:</div>
        <input
          type="text"
          placeholder="name"
          className="inp2"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <div className="inp1">Email:</div>
        <input
          type="email"
          placeholder="Email"
          className="inp2"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <div className="inp1">Password:</div>
        <input
          type="password"
          placeholder="Password"
          className="inp2"
          value={pass}
          onChange={(e) => setPass(e.target.value)}
          required
        />
        <br />
        <div className="inp1">Role:</div>
        <input
          type="radio"
          id="Admin"
          name="role"
          value="Admin"
          checked={role === "Admin"}
          onChange={handleRoleChange}
          className="inp4"
        />
        <label htmlFor="Admin" className="inp5">
          Admin
        </label>
        <br/>
        <input
          type="radio"
          id="Customer"
          name="role"
          value="Customer"
          checked={role === "Customer"}
          onChange={handleRoleChange}
          className="inp4"
        />
        <label htmlFor="Customer" className="inp5">
          Customer
        </label>
        <div className="i3">
          <button type="button" className="button1 inp3" onClick={handleSubmit}>
            Submit
          </button>
        </div>
      </div>
    </>
  );
}

export { Signin };
